mapper.select:
SELECT RECIPE_NO, RECIPE_TITLE, RECIPE_CATEGORY, RECIPE_CONTENT, RECIPE_COUNT, RECIPE_IMG, RECIPE_ENABLED, REGDATE, R_SHORT_DESC FROM ( SELECT ROWNUM RN, A.* FROM( SELECT RECIPE_NO, RECIPE_TITLE, RECIPE_CATEGORY, RECIPE_CONTENT, RECIPE_COUNT, RECIPE_IMG,RECIPE_ENABLED, REGDATE, R_SHORT_DESC FROM RECIPE WHERE
RECIPE_TITLE LIKE '%'||#{keyword}||'%' RECIPE_CONTENT LIKE '%'||#{keyword}||'%' RECIPE_TITLE LIKE '%'||#{keyword}||'%' OR RECIPE_CONTENT LIKE '%'||#{keyword}||'%'
RECIPE_CATEGORY = #{category}
1=1
ORDER BY RECIPE_NO DESC ) A ) WHERE RN > (#{pageNum} - 1) * #{amount} AND RN <= #{pageNum} * #{amount}

SELECT RECIPE_NO, RECIPE_TITLE, RECIPE_CATEGORY, RECIPE_CONTENT, RECIPE_COUNT, RECIPE_IMG,RECIPE_ENABLED, REGDATE
FROM RECIPE
WHERE RECIPE_NO = #{recipe_no}

mapper.update:
UPDATE RECIPE
SET RECIPE_TITLE=#{recipe_title}, RECIPE_CATEGORY=#{recipe_category}, R_SHORT_DESC=#{r_short_desc}, RECIPE_CONTENT=#{recipe_content}
WHERE RECIPE_NO = #{recipe_no}

UPDATE RECIPE
SET RECIPE_COUNT = RECIPE_COUNT + 1
WHERE RECIPE_NO = #{recipe_no}
